mapper.select:

			select * from 
        	(
			    select
			        rownum rnum , A.*
			    from 
			    (SELECT * FROM Product where v_no=#{vno} order by PRO_NO desc) A 
		    	where rownum <= #{pageNum} * #{amount} 
		    )
			where rnum > (#{pageNum}-1) * #{amount}

SELECT pro_no 
FROM product 
WHERE pro_bannerimg = #{PRO_BANNERIMG} 
  AND pro_quantity = #{PRO_QUANTITY} 
  AND pro_price = #{PRO_PRICE} 
  AND v_no = #{V_NO} 
  AND PRO_NAME = #{PRO_NAME}


			select * from 
        	(
			    select
			        rownum rnum , A.*
			    from 
			    (SELECT  * FROM board where b_c_no=#{bcno} and m_no=#{bmno} order by B_NO desc) A 
		    	where rownum <= #{pageNum} * #{amount} 
		    )
			where rnum > (#{pageNum}-1) * #{amount}

SELECT a.pro_no, a.p_c_no, a.pro_price, a.pro_bannerimg, a.pro_wish, a.pro_bizname, a.pro_name, a.avgeval, a.countrv FROM ( SELECT p.*, rownum as rnum, NVL(avg_eval.avg_eval,0) AS avgeval, NVL(rv_count.rv_count, 0) AS countrv FROM product p LEFT JOIN ( SELECT pro_no, AVG(b_eval) AS avg_eval FROM board GROUP BY pro_no ) avg_eval ON p.pro_no = avg_eval.pro_no LEFT JOIN ( SELECT pro_no, COUNT(*) AS rv_count FROM board GROUP BY pro_no ) rv_count ON p.pro_no = rv_count.pro_no WHERE p.v_no = (SELECT v_no FROM vendor WHERE v_bizname = #{bizname})
and p.pro_price between #{start} AND #{end}
order by
${order}
p.pro_sellcount DESC
) a WHERE a.rnum BETWEEN #{pageNum} * 16 - 15 AND #{pageNum} * 16
